const Video = (props) => {
  return (
    <video className="video" autoPlay muted loop>
      <source src={props.videoUrl}  type={props.type} />
    </video>
   );
} 


const  Message = (props) => {
    return (
        <p> {props.message }</p>
    )
}


const Menu = (props) => {

  return (

    <ul className="nav">
      <li>Home</li>
      <li>Blog</li>
      <li>About</li>
      <li>Contact</li>
    </ul>

  )

}

 const Title = (props) => {
   return (
     <h1 className="text">{props.title}</h1>
   )
 };


class App extends React.Component {
  constructor(props){
    super(props);
    this.state ={
      videoUrl: "https://www.youtube.com/watch?v=tST-F75I2To",
      type: "video/mp4"
    }
  }

  render(){
    return (
      <div>
        <Menu />
         <Title title="Women & Men Collection" />
            <Video className="video" videoUrl = {this.state.videoUrl} type =   {this.state.type}/>
        <div className="container">
          <div className="row" style={{marginTop :"60px"}}>
